import{_ as r,c as e,o as t,d as a}from"./app.8037606b.js";const s="/assets/tx-rx.23883e40.webp",o="/assets/TBS-Mambo-Tracer-module-and-PCB-antenna-picture-by-LIVYU-768x520.d5ac2520.jpg",i="/assets/happy-odel-2.4ghz-es24tx-es24tx-Express-fpv-know-it-all-joshua-bardwell.3d561dfb.webp",l="/assets/EP2_RX1.5f7dcc15.jpg",p="/assets/immersion-rc-ghost-atto-reciever-fpv-know-it-all-joshua-bardwell.be715dda.jpg",n="/assets/tx16s-fpv-know-it-all-joshua-bardwell.50fe0a06.jpg",c="/assets/BOXER-001_720x.63baea61.webp",h="/assets/tx12.1c39183a.webp",m="/assets/tango2.774a3f36.jpeg",g="/assets/t-pro.f29a52ff.jpg",d="/assets/t-lite.1da6dcc1.png",_="/assets/zorro.ef2de874.webp",u="/assets/mambo.241337b5.jpeg",f="/assets/lipoTX.63a187c8.jpg",b="/assets/neck.3dbc8562.jpg";const v=JSON.parse('{"title":"Как выбрать аппаратуру радиоуправления?","description":"","frontmatter":{},"headers":[{"level":2,"title":"Резюме","slug":"резюме","link":"#резюме","children":[]},{"level":2,"title":"Протоколы передачи данных","slug":"протоколы-передачи-данных","link":"#протоколы-передачи-данных","children":[]},{"level":2,"title":"Передатчик и приемник","slug":"передатчик-и-приемник","link":"#передатчик-и-приемник","children":[]},{"level":2,"title":"Выбор аппаратуры","slug":"выбор-аппаратуры","link":"#выбор-аппаратуры","children":[]},{"level":2,"title":"Рекомендуемые аппаратуры","slug":"рекомендуемые-аппаратуры","link":"#рекомендуемые-аппаратуры","children":[]},{"level":2,"title":"Необходимые аксессуары","slug":"необходимые-аксессуары","link":"#необходимые-аксессуары","children":[]}],"relativePath":"intro/tx.md"}'),w={name:"intro/tx.md"},R=a('<h1 id="как-выбрать-аппаратуру-радиоуправления" tabindex="-1">Как выбрать аппаратуру радиоуправления? <a class="header-anchor" href="#как-выбрать-аппаратуру-радиоуправления" aria-hidden="true">#</a></h1><blockquote><p>Авторы: <a href="https://github.com/EIIIE" target="_blank" rel="noreferrer">Андрей Щ.</a>, <a href="https://github.com/ikherty" target="_blank" rel="noreferrer">Валентина</a></p></blockquote><h2 id="резюме" tabindex="-1">Резюме <a class="header-anchor" href="#резюме" aria-hidden="true">#</a></h2><p>Аппаратура Радиоуправления (она же Аппаратура РУ, transmitter, пульт, аппа, джойстик) - устройство, преобразующее положения стиков (gimbals)/тумблеров (switches) в команды для коптера. Положение стиков отправляются на радиопередатчик (transmitter, tx, в аппаратуре), преобразующий сигналы в команды определенного протокола передачи данных. Команды передаются коптеру &quot;по воздуху&quot; на определенной частоте (обычно 900 МГЦ или 2.4 ГГЦ). На борту коптера команды принимает радиоприемник (receiver, rx) и отправляет их на полетный контроллер (flight controller, FC) для исполнения.</p><p class="image-container"><img src="'+s+'" width="600" alt="Связь между аппаратурой и дроном" title="Связь между аппаратурой и дроном"></p><h2 id="протоколы-передачи-данных" tabindex="-1">Протоколы передачи данных <a class="header-anchor" href="#протоколы-передачи-данных" aria-hidden="true">#</a></h2><p>Немаловажный момент в выборе радиолинка (RC Link) - протокол, по которому команды передаются на дрон.</p><p>В 2023 году наиболее популярными радио системами являются:</p><p><strong>TBS Crossfire</strong> - дальнобойная система, работающая на частоте 900 МГц. Позволяет ставить рекорды в сотни километров при соблюдении определенного ряда условий (локация, тип ЛА, настройки передатчика, расположение антенн и многое другое). Встречается в качестве внешних передатчиков разных формфакторов и в качестве встроенного модуля аппаратуры <a href="https://www.team-blacksheep.com/products/prod:tbs_tango_2" target="_blank" rel="noreferrer">TBS Tango 2</a>.</p><p>Плюсы и минусы TBS Crossfire:</p><ul><li>На первый взгляд, user friendly программное обеспечение для настройки и обновления прошивки. Очень подробная документация, отзывчивая поддержка. Однако при выходе новых версий периодически возникают проблемы с коннектом, о которых в явном виде можно узнать только по чатам или на личном опыте.</li><li>В теории, при идеальных условиях, управление с помощью этой системы будет еще некоторое время оставаться после обрыва видеосвязи. Поскольку частота ниже и длина волны больше, чем у стандартных радиопротоколов на 2,4 ГГц и самой распространенной видеосвязи на частоте 5,8 ГГц. Но это не гарантирует, что вы никогда не потеряете связь с аппаратурой до проблем с видео.</li><li>Система является закрытой, достаточно сложно убедить производителя что-либо исправить или самому доработать.</li><li>Не подходит для гонок на профессиональном уровне из-за ощутимых (для топ пилотов) задержек передачи команд, так как работает на частоте 900 МГЦ. В целом, антенны приемников достаточно габаритные, это не прибавляет удобств.</li><li>Дорого в сравнении с некоторыми аналогами, поскольку особо нет конкурентов в том же сегменте.</li><li>Корректно работает только на частоте отправки пакетов в 50 Гц. При выборе 150 Гц нередко теряет связь уже в 100 метрах.</li><li>Подойдет любителям ставить рекорды на дальность и летать в сложных условиях.</li></ul><p><a href="https://www.expresslrs.org/" target="_blank" rel="noreferrer"><strong>ELRS (он же ExpressLRS - Express Long Range System)</strong></a> - система, созданная самим сообществом. Есть версия на 900 МГц и 2,4 ГГц. Рекомендуется версия на 2,4 ГГЦ. Встречаются в качестве внешних передатчиков разных формфакторов и в качестве встроенного модуля аппаратур от radiomaster, jumper и других (менее популярных).</p><p>Плюсы и минусы ELRS:</p><ul><li>Коды и схемы в открытом доступе, что мотивировало производителей железа выпускать приемники, передатчики, полетные контроллеры со встроенным приемником, и появилась высокая конкуренция. Как результат - лучший вариант по соотношению цена/качество, самое актуальное железо по минимальной цене.</li><li>Дальность передачи команд легко превышает дальность работы видеопотока (умельцы летают десятки километров, на данный момент рекорд 100 км, посмотреть таблицу с тестами можно здесь: <a href="https://www.expresslrs.org/2.0/info/long-range" target="_blank" rel="noreferrer">https://www.expresslrs.org/2.0/info/long-range</a>)</li><li>Минимальные задержки передачи данных, поскольку можно выбрать частоту отправки пакетов вплоть до 500 Гц -&gt; отлично подходит для гонок (но учтите, что дальность при этом уменьшается).</li><li>Среди доступных на рынке приемников есть линейка самых легких и компактных (см. <a href="https://www.happymodel.cn/index.php/2021/04/10/happymodel-2-4g-expresslrs-elrs-nano-series-receiver-module-pp-rx-ep1-rx-ep2-rx/" target="_blank" rel="noreferrer">happymodel ep2</a>).</li><li>Программное обеспечение постепенно улучшается, упрощается для пользователя. Документация также обширна и подробна. Для избежания проблем с прошивками рекомендуется брать наиболее популярное железо с наименьшим количеством &quot;косяков&quot; за время роста проекта (например, happymodel). Про косяки можно спросить в чатах и у сообщества.</li><li>Новое железо от производителей появляется каждый месяц и разработчики не всегда успевают выпускать софт для них или исправлять ошибки. Поэтому надо быть готовым к тому, что с некоторыми платами придется подождать прошивок или исправлений. Если вы нашли проблему, можете сообщить о ней или поискать по ключевым словам в <a href="https://github.com/ExpressLRS/ExpressLRS" target="_blank" rel="noreferrer">репозитории ExpressLRS</a> заведенные заявки (issues) и обязательно получите решение в ближайшее время. Также есть чат, где без проблем помогут - <a href="https://t.me/expresslrs_rus" target="_blank" rel="noreferrer">@expresslrs_rus</a></li></ul><p>Конкуренты ELRS, но с закрытыми прошивками:</p><p><a href="https://www.team-blacksheep.com/tracer/" target="_blank" rel="noreferrer"><strong>TBS Tracer</strong></a> - если кратко: &quot;натянули Crossfire на гонщиков&quot;. Работает на 2,4 ГГЦ (Crossfire медленнее - 900 МГц), по удобству схож с ним. Не такой быстрый как ELRS, не такой дальнобойный как Crossfire, первое время были проблемы с дальностью. Cо временем вышли новые прошивки с исправлением, но все еще имеет место быть. Встречается в виде отдельного модуля и в качестве встроенного передатчика аппаратуры <a href="https://www.team-blacksheep.com/products/prod:tbs_mambo" target="_blank" rel="noreferrer">TBS Mambo</a>.</p><p><a href="https://www.immersionrc.com/fpv-products/ghost/" target="_blank" rel="noreferrer"><strong>IMMERSION RC GHOST</strong></a> - соперник ELRS по минимизации задержек передачи команд. Наиболее распространен в штатах, вероятно, ввиду доступности. В основном используется топ гонщиками. Встречается только в качестве внешнего передатчика.</p><blockquote><p>Устаревшее и что <strong>не стоит брать</strong> с 2020 года для дронов: frsky, flysky, dsmx (spektrum)...</p></blockquote><p>Как правило, новичкам рекомендуется начинать с ELRS - дешевый и доступный вход. Но если не важна задержка и планируется в основном Long Range на различных сложных локациях - можно посмотреть в сторону TBS Crossfire.</p><p>Подробно про протоколы - <a href="https://oscarliang.com/rc-protocols/" target="_blank" rel="noreferrer">https://oscarliang.com/rc-protocols/</a> (английский язык)</p><h2 id="передатчик-и-приемник" tabindex="-1">Передатчик и приемник <a class="header-anchor" href="#передатчик-и-приемник" aria-hidden="true">#</a></h2><p>Аппаратура и коптер общаются между собой при помощи передатчика и приемника. Передатчик в аппаратуре может быть как встроенным (распаянным на плате), так и внешним (вставляющимся в слот на задней части аппаратуры). Встроенный модуль, как правило, удобнее ввиду компактности, в некоторых случаях потребляет меньше питания, чем внешний. Максимальная мощность внешнего передатчика может быть ограничена 250мВт. Однако этого достаточно даже для 50 км. Если глянуть <a href="https://www.expresslrs.org/software/switch-config/" target="_blank" rel="noreferrer">таблицу с тестами на сайте ELRS</a>, то в некоторых случаях может быть мало.</p><blockquote><p>Ниже представлены аппаратура без кожуха и внешний модуль ELRS. На плате можно найти модуль-передачтик. Внешний же модуль-передатчик устанавливается в специальный разъём снаружи аппаратуры</p></blockquote><p class="image-container"><img src="'+o+'" height="300" alt="Аппаратура без кожуха" title="Аппаратура без кожуха"><img src="'+i+'" height="400" alt="Внешний модуль ELRS" title="Внешний модуль ELRS"></p><p>Приемник - это небольшая плата с антенной(ами), устанавливающаяся на дрон и подключающаяся к его полетному контроллеру на <a href="https://ru.wikipedia.org/wiki/%D0%A3%D0%BD%D0%B8%D0%B2%D0%B5%D1%80%D1%81%D0%B0%D0%BB%D1%8C%D0%BD%D1%8B%D0%B9_%D0%B0%D1%81%D0%B8%D0%BD%D1%85%D1%80%D0%BE%D0%BD%D0%BD%D1%8B%D0%B9_%D0%BF%D1%80%D0%B8%D1%91%D0%BC%D0%BE%D0%BF%D0%B5%D1%80%D0%B5%D0%B4%D0%B0%D1%82%D1%87%D0%B8%D0%BA" target="_blank" rel="noreferrer">UART (Universal Asynchronous Receiver-Transmitter, универсальный асинхронный приёмопередатчик)</a>.</p><blockquote><p>Ниже представлены приемник Happymodel EP2 ELRS с керамической антенной и приемник Ghost с диполь антенной</p></blockquote><p class="image-container"><img style="height:300px;" src="'+l+'" alt="Приемник Happymodel ELRS 2.4GHz" title="Приемник Happymodel ELRS 2.4GHz"><img style="height:300px;" src="'+p+'" alt="Приемник Ghost" title="Приемник Ghost"></p><p><strong>Не важны различия в производителях передатчика и приемника, пока они оба работают на одном протоколе. Но если речь идет про ELRS, лучше изучить рекомендации и тесты железа разных производителей.</strong></p><h2 id="выбор-аппаратуры" tabindex="-1">Выбор аппаратуры <a class="header-anchor" href="#выбор-аппаратуры" aria-hidden="true">#</a></h2><p>Обновляемая статья на английском языке про аппаратуры, достойная внимания - <a href="https://oscarliang.com/radio-transmitter/" target="_blank" rel="noreferrer">https://oscarliang.com/radio-transmitter/</a></p><p>Краткий перечень на английском языке без &quot;воды&quot; - <a href="https://www.fpvknowitall.com/fpv-shopping-list-controller-and-receiver/" target="_blank" rel="noreferrer">https://www.fpvknowitall.com/fpv-shopping-list-controller-and-receiver/</a></p><p>Если в статьях выше нет аппаратуры, которая вас интересует, то либо она слишком новая, либо мало предпочитаемая сообществом.</p><h2 id="рекомендуемые-аппаратуры" tabindex="-1">Рекомендуемые аппаратуры <a class="header-anchor" href="#рекомендуемые-аппаратуры" aria-hidden="true">#</a></h2><p>Настоятельно рекомендуется рассматривать аппаратуры, работающие на ПО OpenTX (потомком которого является EdgeTX). Это как операционная система компьютера. С ним ты будешь уверен, что спокойно подключишь к симулятору и будешь продолжать получать обновления с новыми фишками.</p><p><a href="https://www.radiomasterrc.com/products/tx16s-mark-ii-radio-controller" target="_blank" rel="noreferrer"><strong>RADIOMASTER TX16S</strong></a> - Если у тебя большие руки, нужно много свитчей (переключателей, тумблеров, потов/крутилок), хочется (зачем-то) большой экран.</p><p>Цены обычно от 200$, есть версии со встроенным ELRS (внимательно читайте описание, не путайте с мультимодулем (4in1, cc2500), который не включает в себя ELRS и Crossfire). Можно поставить внешний передатчик по вкусу (30-250$). Существует в различных модификациях - цветные корпусы, алюминиевые стики, с тачскрином. Дополнительно разноцветные моды есть, в целом, у всех аппаратур от radiomaster.</p><p class="image-container"><img src="'+n+'" height="400" alt="Аппаратура RADIOMASTER TX16S" title="Аппаратура RADIOMASTER TX16S"></p><p><a href="https://www.radiomasterrc.com/products/boxer-radio-controller-m2?_pos=1&amp;_sid=194aad403&amp;_ss=r" target="_blank" rel="noreferrer"><strong>RADIOMASTER BOXER</strong></a> - Средний вариант по габаритам с полноценными стиками, минимально необходимым количеством свитчей, черно-белый экран (это не недостаток, он нужен только для отображения меню настроек и моделей)</p><p>Есть также вариант со встроенным ELRS модулем на 1Вт и мультимодулями. Цена 130-160$. Считается компромисом между tx16 и компактными вариантами, которые ниже по списку. Подойдет большинству начинающих и не только.</p><p class="image-container"><img src="'+c+'" height="400" alt="Аппаратура RADIOMASTER Boxer" title="Аппаратура RADIOMASTER Boxer"></p><p><a href="https://www.radiomasterrc.com/products/tx12-mark-ii-radio-controller?_pos=1&amp;_sid=02bbb6530&amp;_ss=r" target="_blank" rel="noreferrer"><strong>RADIOMASTER TX12 mk2</strong></a> - Компактная версия &quot;кирпичика&quot; с мини версией стиков.</p><p>Цены обычно от 80$, есть версии со встроенным ELRS (внимательно читайте описание, можно поставить внешний передатчик по вкусу (30-250$)). Отдельно можно приобрести цветные корпусы, алюминиевые стики. Также неплохой вариант для начала, когда не понятно, что хочется, и нужна компактность. Легко будет продать в любой момент на барахолке, если по какой-либо причине &quot;не зайдет&quot;.</p><p class="image-container"><img src="'+h+'" height="400" alt="Аппаратура RADIOMASTER TX12" title="Аппаратура RADIOMASTER TX12"></p><p><a href="https://www.team-blacksheep.com/products/prod:tbs_tango_2" target="_blank" rel="noreferrer"><strong>TBS TANGO 2</strong></a> - Компактный вариант со строенным crossfire передатчиком. Подходит, если цель путешествовать налегке. Не путать с первой версией 😃</p><p>Есть обычная версия за 160$ и pro за 200$. В России цены от 20 тыс. руб., продавцы объсняют такую разницу сложностями поставок. Основное отличие pro версии в складывающихся стиках, но явного преимущества в этом нет. Встроенный передатчик TBS CROSSFIRE, мощность зависит от версии платы. При особой любви и необходимости можно установить внешний передатчик. Также есть вариант встроить ELRS модуль с небольшой мощностью для полетов неподалеку, как <a href="https://www.youtube.com/watch?v=2cn96u_nlnw" target="_blank" rel="noreferrer">в этом видео</a>. Крайне маленький экран, но для полетов в режиме FPV он и не нужен. Кнопки вместо тумблеров - дело на любителя, но не всегда получается моментально &quot;задизармить&quot; коптер (то есть выключить моторы). Бывают проблемы со стиками: поскольку используется &quot;супер технология&quot; с одним датчиком на 2 оси, калибровку надо производить строго по инструкции производителя. Сами стики &quot;разведены&quot; на материнской плате, поэтому нет возможности легко заменить их на другие, как в случае с аппаратурами Radiomaster. Иногда бывают проблемы с глюками экрана, &quot;окирпичиванием&quot; модуля при прошивке, требованием калибровки при запуске. Но, несмотря на это, все еще пользуется популярностью.</p><p class="image-container"><img src="'+m+'" height="400" alt="аппаратура TBS Tango 2"></p><p><strong><a href="https://www.jumper-rc.com/products/transmitters/t-pro/" target="_blank" rel="noreferrer">Jumper T-Pro</a>, <a href="https://www.jumper-rc.com/products/transmitters/t-lite/" target="_blank" rel="noreferrer">Jumper T-Lite</a>, <a href="https://www.radiomasterrc.com/products/zorro-radio-controller?_pos=1&amp;_sid=49612d66c&amp;_ss=r" target="_blank" rel="noreferrer">Radiomaster Zorro</a></strong> - когда хочется аналог TBS Tango со встроенным ELRS</p><p>Цены 50-100$, некий компромисс, когда хочется компактности, есть версии со встроенным модулем ELRS и мультимодулем, не путать! У всех претендентов есть свои плюсы и минусы, рекомендуется глянуть обзоры перед покупкой. У <a href="https://github.com/ikherty" target="_blank" rel="noreferrer">Валентины</a> аппаратура Zorro. Главный недостаток этой аппаратуры - аккумуляторы 18350 (нестандартный труднодоступный вариант Li-Ion аккумуляторов). Сначала Валентиной были приобретены не самые качественные ячейки, через полгода использования аккумуляторы не держали заряд и 20 минут, пришлось использовать внешний аккумулятор, пока не были куплены ячейки 18350 от Radiomaster. По наблюдениям и личному опыту с ними заряда хватает на 2-3 часа, но будь они емкостью не 900мАч, а 3000мАч, как типовые 18650, то про зарядку приходилось бы вспоминать еще реже. По габаритам аппаратура как Radiomaster TX12. Eсли бы они в версии ELRS вышли одновременно, то приобрела бы ТХ12, поскольку там удобные 18650 ячейки Li-Ion. Аппаратуры Jumper T-Pro и Jumper T-Lite подойдут тем, кто хочет минимальных вложений для входа в хобби (а вдруг не понравится/не получится - зачем тратиться). Но не нужно думать, что данные аппаратуры совсем плохи, ввиду их компактности и наличия встроенного ELRS модуля немалое количество пилотов покупает их в качестве второй аппаратуры. Они легко поддаются модификациям: замена антенны, стиков (на примере t-lite <a href="https://youtu.be/wslSmuW3DDI" target="_blank" rel="noreferrer">https://youtu.be/wslSmuW3DDI</a>). Это позволяет кастомизировать аппу и улучшить некоторые ее детали, однако компонентная база платы аппаратуры &quot;скудная&quot; и надо быть готовым, что в один момент что-то может выйти из строя.</p><p class="image-container"><img src="'+g+'" height="300" alt="Аппаратура Jumper T-Pro" title="Аппаратура Jumper T-Pro"><img src="'+d+'" height="300" alt="Аппаратура Jumper T-Lite" title="Аппаратура Jumper T-Lite"><img src="'+_+'" height="300" alt="Аппаратура RadioMaster ZORRO" title="Аппаратура RadioMaster ZORRO"></p><p><a href="https://www.team-blacksheep.com/products/prod:tbs_mambo" target="_blank" rel="noreferrer">TBS Mambo</a> - Типовой кирпичик для фанатов TBS Tracer</p><p>140$, в России часто от 18 тыс. руб.. Встроенный передатчик TBS Tracer. У аппаратуры большое количество свитчей, полноценный черно-белый экран, есть слот под внешний передатчик. Есть мнение, что с мамбо срисован RadioMaster Boxer - формфактор практически идентичен, расположение свитчей схоже. Из недостатков: ощущается дешевле ранее упомянутых аппаратур, стики как у танго 2. Формфактор кирпичика на любителя.</p><p class="image-container"><img src="'+u+'" height="400" alt="Аппаратура TBS Mambo" title="Аппаратура TBS Mambo"></p><h2 id="необходимые-аксессуары" tabindex="-1">Необходимые аксессуары <a class="header-anchor" href="#необходимые-аксессуары" aria-hidden="true">#</a></h2><ul><li>Аккумулятор - если в аппаратуре нет встроенной батареи, то необходимо обзавестись подходящей по размеру в аппаратуру. Как правило используются сборки 2S или две ячейки Li-Ion 18650 в переходнике под 2S. <blockquote><p>Отдельно прикрепляем ссылку на <a href="https://propwashservice.ru/intro/power.html" target="_blank" rel="noreferrer">статью про аккумуляторы для дронов</a></p></blockquote></li></ul><p class="image-container"><img src="'+f+'" height="200" alt="Аккумуляторная батарея, совместимая с RadioMaster TX16S" title="Аккумуляторная батарея, совместимая с RadioMaster TX16S"></p><ul><li>Ремешок для шеи - держать аппаратуру в руках на весу в течении пары часов может быть ещё тем испытанием. Рекомендуется, чтобы у ремешка был Fastex (пластиковый карабин посередине) для случаев, когда необходимо положить аппаратуру, не снимая сам ремешок через голову, на которой наверняка FPV очки.</li></ul><p class="image-container"><img src="'+b+'" height="200" alt="Ремешок для шеи" title="Ремешок для шеи"></p>',57),S=[R];function k(T,D,x,B,E,L){return t(),e("div",null,S)}const A=r(w,[["render",k]]);export{v as __pageData,A as default};
